import React, { useState, useEffect } from 'react';
import { useAuthStore } from '../../store/authStore';

const EmployeeDashboard = () => {
  const { user, company } = useAuthStore();
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isCheckedIn, setIsCheckedIn] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const handleAttendance = () => {
    setIsCheckedIn(!isCheckedIn);
    // TODO: Implement actual attendance API call
  };

  const stats = [
    {
      title: 'Present Days',
      value: '22',
      subtitle: 'This month',
      icon: '‚úÖ',
      color: 'bg-green-500'
    },
    {
      title: 'Leave Balance',
      value: '8',
      subtitle: 'Days remaining',
      icon: 'üèñÔ∏è',
      color: 'bg-blue-500'
    },
    {
      title: 'Pending Tasks',
      value: '5',
      subtitle: 'Due this week',
      icon: 'üìã',
      color: 'bg-orange-500'
    },
    {
      title: 'This Month Salary',
      value: '‚Çπ45,000',
      subtitle: 'Gross amount',
      icon: 'üí∞',
      color: 'bg-purple-500'
    }
  ];

  const recentActivities = [
    {
      type: 'attendance',
      message: 'Checked in at 9:15 AM',
      time: '2 hours ago',
      icon: '‚è∞'
    },
    {
      type: 'leave',
      message: 'Leave application approved',
      time: '1 day ago',
      icon: '‚úÖ'
    },
    {
      type: 'task',
      message: 'Task "Project Review" completed',
      time: '2 days ago',
      icon: '‚úÖ'
    },
    {
      type: 'payroll',
      message: 'Salary slip generated for October',
      time: '3 days ago',
      icon: 'üí∞'
    }
  ];

  const upcomingEvents = [
    {
      title: 'Team Meeting',
      date: 'Today, 2:00 PM',
      type: 'meeting'
    },
    {
      title: 'Project Deadline',
      date: 'Tomorrow',
      type: 'deadline'
    },
    {
      title: 'Company Holiday',
      date: 'Nov 15, 2024',
      type: 'holiday'
    }
  ];

  return (
    <div className="space-y-6">
      {/* Quick Actions */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {/* Attendance */}
          <div className="text-center">
            <button
              onClick={handleAttendance}
              className={`w-full p-4 rounded-lg border-2 border-dashed transition-colors ${
                isCheckedIn
                  ? 'border-red-300 bg-red-50 hover:bg-red-100'
                  : 'border-green-300 bg-green-50 hover:bg-green-100'
              }`}
            >
              <div className="text-2xl mb-2">
                {isCheckedIn ? 'üî¥' : 'üü¢'}
              </div>
              <div className="text-sm font-medium">
